import React from 'react';
import './index.css';
import Navbar from "./components/Navbar"
import {useState} from "react"


function App() {
  const [inputData, setInputData] = useState({})
  const [currentWeather, setCurrentWeather] = useState([])
  const [loc, setloc] = useState("Arlington")
  let apiKey = "7ab98e97faa64c4d8b4104902222202"
  let lat = "34.51"
  let lon = "-105.87"
  console.log(loc)

  React.useEffect(() =>{
    fetch(`https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${loc}&aqi=no`)
    .then(res => res.json())
    .then(data => setCurrentWeather(data))
  }, [loc])

  React.useEffect(() =>{
    fetch(`https://api.weatherapi.com/v1/search.json?key=${apiKey}&q=${loc}&aqi=no`)
    .then(res => res.json())
    .then(data => console.log(data))
  }, [loc])

  console.log(currentWeather)



  return (
    <div className="App">
      <Navbar inputData={inputData} setloc={setLoc} setInputData={setInputData}/>
      <p>{inputData.loc}</p>
    </div>
  );
}

export default App;
