import {useState} from "react"

export default function Navbar({loc, inputData, setLoc, setInputData}){

    function handleChange(event){
        const {name, value} = event.target
        setInputData(prevEntry =>({
            ...prevEntry,
            [name]: value
        }))
        if(loc.loc == null){
            setInputData(prev =>({
                ...prev,
                [name]: "Arlington"
            }))
        }
    }
    function submitLoc(event){///Submits entered location and set loc to entry
        event.preventDefault()
        setLoc(inputData)
        console.log("Help" + inputData.loc)

        if(inputData.loc == null){
            const {name, value} = event.target
            setInputData(prev =>({
                ...prev,
                [name]: "Arlington"
            }))
        }
    }

    return(
        <form onSubmit={submitLoc} className="navbar">
            <input name="loc" onChange={handleChange} className="location-entry" type="text" placeholder="Location" />
            <button type="submit">Submit</button>
        </form>
    )
}
