//If the substring of (10,11) == a space, then use currentTime.substring(surrentTime.length-3)
//Else use currenttime.length-4

import {useState, useEffect} from "react"
//  props.currentWeather?.location?.localtime

export default function CurrentWeather(props){
    const [currentTime, setCurrentTime] = useState(props.currentWeather?.location?.localtime)
    console.log("current time: " + currentTime)

    useEffect(() =>{
        if(currentTime != undefined && currentTime.length == 16){
            setCurrentTime(currentTime.substring(currentTime.length-3))

        }else{
            setCurrentTime(currentTime.substring(currentTime.length-4))
            console.log(currentTime.substring(5,8))

        }        

    }, [props.loc])


    return(
        <div className="weather-section weather-block">
            <h1 className="weather-block-title">Current Weather</h1>
            <p className="weather-time">{currentTime}</p>
            <h1 className="farenheit">{Math.round(props.currentWeather?.current?.temp_f)}{"\u00B0"}</h1>
        </div>
    )
}
