import {useState} from "react"

export default function Navbar({inputData, setLocation, setInputData}){

    function handleChange(event){
        const {name, value} = event.target
        setInputData(prevEntry =>({
            ...prevEntry,
            [name]: value
        }))
    }
    function submitLocation(event){
        event.preventDefault()

        setLocation(inputData)
    }

    return(
        <form onSubmit={submitLocation} className="navbar">
            <input name="location" onChange={handleChange} className="location-entry" type="text" placeholder="Location" />
        </form>
    )
}
