///currentTime is behind props.currentWeather?.location?.localtime

import {useState, useEffect} from "react"

export default function CurrentWeather(props){
    const [currentTime, setCurrentTime] = useState(props.currentWeather?.location?.localtime)
    console.log("current time: " + currentTime)
    console.log(props.currentWeather?.location?.localtime)

    useEffect(() =>{
        console.log(currentTime.length)
        if(props.currentWeather?.location?.localtime.length == 16){
            setCurrentTime(props.currentWeather?.location?.localtime.substring(props.currentWeather?.location?.localtime.length-6))
            console.log("HERE")
        }else{
            setCurrentTime(props.currentWeather?.location?.localtime.substring(props.currentWeather?.location?.localtime.length-4))
            console.log("THERE")
        }        

    }, [props.loc])


    return(
        <div className="weather-section weather-block">
            <h1 className="weather-block-title">Current Weather</h1>
            <p className="weather-time">{currentTime}</p>
            <h1 className="farenheit">{Math.round(props.currentWeather?.current?.temp_f)}{"\u00B0"}</h1>
        </div>
    )
}
