    }, [props.loc])


    console.log("current time: " + currentTime)
    console.log(props.currentWeather?.location?.localtime)
    console.log(props.currentWeather?.current?.condition.icon)


    return(
        <div className="weather-section weather-block">
            <div className="weather-section-left">
                <h1 className="weather-block-title">Current Weather</h1>
                <p className="weather-time">{props.currentWeather?.location?.localtime.substring(11)}</p>
                <div className="weather-section-left-bottom"> 
                    <img className="weather-section-image" src={props.currentWeather?.current?.icon}></img>
                    <h1 className="farenheit">{Math.round(props.currentWeather?.current?.condition?.temp_f)}{"\u00B0"}</h1>
                    <p className="f-letter">F</p>
                </div>                
            </div>
        </div>
    )
}


